import React from 'react';
import logo from './logo.svg';
import './App.css';

import Card from "./components/Card" 

function App() {
    //variable declaration of card's active state
    const [cardActive, setCardActiveState] = useState(false);

    //function that toggles card active state
    toggleCardActive = () => {
        setCardActiveState(!cardActive)
    }


  return (
    <div className="App">
      <Card
        title = "Lake Inniscarra, Ireland-- pyramid"
        time = "30:53"
        distance = "6,248 M"
        thumbnail = ""
        image = ""
        workouts = "none"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />
      <Card
        title = "Performance Series"
        time = "none"
        distance = "none"
        thumbnail = ""
        image = ""
        workouts = "9"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />
      <Card
        title = "Show Pulls and Fast Intervals"
        time = "44:13"
        distance = "9,948 M"
        thumbnail = ""
        image = ""
        workouts = "none"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />
        <Card
        title = "20 Minutes to Toned"
        time = "none"
        distance = "none"
        thumbnail = ""
        image = ""
        workouts = "12"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />
      <Card
        title = "Charles Race, Boston, Massachusetts"
        time = "36:22"
        distance = "8,648 M"
        thumbnail = ""
        image = ""
        workouts = "none"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />
      <Card
        title = "Full-Body HIIT Series"
        time = "none"
        distance = "none"
        thumbnail = ""
        image = ""
        workouts = "12"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />
      <Card
        title = "Kafue River, Zambia-- Power"
        time = "30:53"
        distance = "6,248 M"
        thumbnail = ""
        image = ""
        workouts = "none"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />
      <Card
        title = "Lake Inniscarra, Ireland-- pyramid"
        time = "30:53"
        distance = "6,248 M"
        thumbnail = ""
        image = ""
        workouts = "none"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      />

    </div>
  );
}

export default App;
