import React from 'react';
import logo from './logo.svg';
import './App.css';

import Card from "./components/Card" 

function App() {
    //variable declaration of card's active state
    const [cardActive, setCardActiveState] = useState(false);

    //function that toggles card active state
    toggleCardActive = () => {
        setCardActiveState(!cardActive)
    }


  return (
    <div className="App">
      <Card
        title = "Lake Inniscarra, Ireland-- pyramid"
        time = "30:53"
        distance = "6,248 M"
        thumbnail = ""
        image = ""
        workouts = "none"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      ></Card>
      <Card
        title = "Lake Inniscarra, Ireland-- pyramid"
        time = "30:53"
        distance = "6,248 M"
        thumbnail = ""
        image = ""
        workouts = "none"
        active = {cardActive}
        toggleCardActive = {toggleCardActive}
      ></Card>

    </div>
  );
}

export default App;
